import { StyleSheet } from "@react-pdf/renderer";

export const styles = StyleSheet.create({
  // App.js
  container: {
    backgroundColor: 'red',
    alignItems: 'center'
  },  
  // PDFFile.js
  body: {
    paddingTop: 35,
    paddingBottom: 65,
    paddingHorizontal: 35,
  },
  title: {
    fontSize: 24,
    textAlign: "center",
  },
  myID: {
    fontSize: 12,
    textAlign: "center",
    marginTop: 5,
    marginBottom: 5
  },
  header: {
    fontSize: 12,
    marginBottom: 20,
    textAlign: "center",
    color: "grey",
  },
  pageNumber: {
    position: "absolute",
    fontSize: 12,
    bottom: 30,
    left: 0,
    right: 0,
    textAlign: "center",
    color: "grey",
  },
});
